r.x:
var $bm_rt;
var ctrlLayer = thisComp.layer('C | Head rotation');
var connectorLayer = thisComp.layer('C | Connector');
var ctrlValue = ctrlLayer.effect('Slider')(16);
var ctrlEffect = connectorLayer.effect('C | Slider # Value');
var ctrlMin = ctrlEffect(2).value;
var ctrlMax = ctrlEffect(3).value;
var result = value;
if (numKeys >= 2 && ctrlEffect.enabled) {
    var useVelocity = ctrlEffect(1).value == 2;
    if (useVelocity)
        ctrlValue = ctrlValue.velocity;
    else
        ctrlValue = ctrlValue.value;
    var t = 0;
    var beginTime = key(1).time;
    var endTime = key(numKeys).time;
    if (ctrlMin > ctrlMax) {
        t = linear(ctrlValue, ctrlMin, ctrlMax, endTime, beginTime);
    } else {
        t = linear(ctrlValue, ctrlMin, ctrlMax, beginTime, endTime);
    }
    result = valueAtTime(t);
}
$bm_rt = result;

